import React, { useState, useContext } from 'react';
import { motion } from 'framer-motion';
import ProductCard from '../components/products/ProductCard';
import { ProductsContext } from '../context/ProductsContext';
import './ProductsPage.css';

const ProductsPage = () => {
  const { products, loading, addProduct } = useContext(ProductsContext);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [showAddForm, setShowAddForm] = useState(false);

  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    category: '',
    image: '',
    description: ''
  });

  // Unique categories
  const categories = ['All', ...new Set(products.map(product => product.category))];

  const filteredProducts =
    selectedCategory === 'All'
      ? products
      : products.filter(product => product.category === selectedCategory);

  const handleChange = e => {
    setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
  };

  const handleAddProduct = e => {
    e.preventDefault();
    if (!newProduct.name || !newProduct.price || !newProduct.category) {
      alert('Please fill all required fields');
      return;
    }
    addProduct(newProduct);
    setNewProduct({ name: '', price: '', category: '', image: '', description: '' });
    setShowAddForm(false);
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: { staggerChildren: 0.1 }
    }
  };

  return (
    <motion.div
      className="products-page"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
    >
      <div className="container">
        <motion.h1
          className="page-title"
          initial={{ y: -50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ type: 'spring', stiffness: 100 }}
        >
          Our Products
        </motion.h1>

        {/* ðŸ”¹ Add Product Button */}
        <div className="add-product-header">
          <button className="add-btn" onClick={() => setShowAddForm(!showAddForm)}>
            {showAddForm ? 'Close Form' : 'âž• Add Product'}
          </button>
        </div>

        {/* ðŸ”¹ Add Product Form */}
        {showAddForm && (
          <motion.form
            className="add-product-form"
            initial={{ y: -20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            onSubmit={handleAddProduct}
          >
            <input
              type="text"
              name="name"
              placeholder="Product Name"
              value={newProduct.name}
              onChange={handleChange}
              required
            />
            <input
              type="number"
              name="price"
              placeholder="Price"
              value={newProduct.price}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="category"
              placeholder="Category"
              value={newProduct.category}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="image"
              placeholder="Image URL"
              value={newProduct.image}
              onChange={handleChange}
            />
            <textarea
              name="description"
              placeholder="Description"
              value={newProduct.description}
              onChange={handleChange}
            />
            <button type="submit" className="submit-btn">Add Product</button>
          </motion.form>
        )}

        <div className="products-container">
          <motion.div
            className="filters"
            initial={{ x: -50, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ delay: 0.2 }}
          >
            <h3>Categories</h3>
            <ul className="category-list">
              {categories.map((category, index) => (
                <motion.li key={index} whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
                  <button
                    className={selectedCategory === category ? 'active' : ''}
                    onClick={() => setSelectedCategory(category)}
                  >
                    {category}
                  </button>
                </motion.li>
              ))}
            </ul>
          </motion.div>

          <motion.div
            className="products-grid"
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            {loading ? (
              <div className="loading">Loading products...</div>
            ) : filteredProducts.length > 0 ? (
              filteredProducts.map(product => (
                <ProductCard key={product._id || product.name} product={product} />
              ))
            ) : (
              <div className="no-products">No products found in this category.</div>
            )}
          </motion.div>
        </div>
      </div>
    </motion.div>
  );
};

export default ProductsPage;
