import React, { useState } from 'react';
import { motion } from 'framer-motion';
import ProductCard from '../components/products/ProductCard';
import './ProductsPage.css';

const ProductsPage = () => {
  // Mock products data (would come from API in real app)
  const [products] = useState([
    {
      _id: '1',
      name: 'Handcrafted Pottery',
      description: 'Beautiful handmade pottery from rural artisans',
      price: 1299,
      image: '/images/pottery.jpg',
      category: 'Home Decor'
    },
    {
      _id: '2',
      name: 'Organic Tea Collection',
      description: 'Premium organic tea sourced from the hills of Darjeeling',
      price: 599,
      image: '/images/tea.svg',
      category: 'Food'
    },
    {
      _id: '3',
      name: 'Handwoven Scarf',
      description: 'Elegant handwoven scarf made from sustainable materials',
      price: 899,
      image: '/images/scarf.svg',
      category: 'Fashion'
    },
    {
      _id: '4',
      name: 'Brass Decor Items',
      description: 'Traditional brass items for home decoration',
      price: 1499,
      image: 'https://images.unsplash.com/photo-1580480055273-228ff5388ef8?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=2670&q=80',
      category: 'Home Decor'
    },
    {
      _id: '5',
      name: 'Handmade Soap Set',
      description: 'Natural handmade soaps with essential oils',
      price: 499,
      image: 'https://images.unsplash.com/photo-1607006344380-b6775a0824ce?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=2671&q=80',
      category: 'Beauty'
    },
    {
      _id: '6',
      name: 'Embroidered Cushion Cover',
      description: 'Hand-embroidered cushion covers with traditional designs',
      price: 799,
      image: 'https://images.unsplash.com/photo-1616486338812-3dadae4b4ace?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=2532&q=80',
      category: 'Home Decor'
    }
  ]);

  const [loading] = useState(false);
  const [categories] = useState([
    'All',
    'Home Decor',
    'Food',
    'Fashion',
    'Beauty'
  ]);
  const [selectedCategory, setSelectedCategory] = useState('All');

  const filteredProducts = selectedCategory === 'All'
    ? products
    : products.filter(product => product.category === selectedCategory);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  return (
    <motion.div 
      className="products-page"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
    >
      <div className="container">
        <motion.h1 
          className="page-title"
          initial={{ y: -50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ type: 'spring', stiffness: 100 }}
        >
          Our Products
        </motion.h1>

        <div className="products-container">
          <motion.div 
            className="filters"
            initial={{ x: -50, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ delay: 0.2 }}
          >
            <h3>Categories</h3>
            <ul className="category-list">
              {categories.map((category, index) => (
                <motion.li 
                  key={index}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <button 
                    className={selectedCategory === category ? 'active' : ''}
                    onClick={() => setSelectedCategory(category)}
                  >
                    {category}
                  </button>
                </motion.li>
              ))}
            </ul>
          </motion.div>

          <motion.div 
            className="products-grid"
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            {loading ? (
              <div className="loading">Loading products...</div>
            ) : filteredProducts.length > 0 ? (
              filteredProducts.map(product => (
                <ProductCard key={product._id} product={product} />
              ))
            ) : (
              <div className="no-products">No products found in this category.</div>
            )}
          </motion.div>
        </div>
      </div>
    </motion.div>
  );
};

export default ProductsPage;
