import React, { useState, useContext, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { FaShoppingCart, FaMinus, FaPlus, FaArrowLeft } from 'react-icons/fa';
import { CartContext } from '../context/CartContext';
import { ProductsContext } from '../context/ProductsContext';
import './ProductDetailPage.css';

const ProductDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useContext(CartContext);
  const { products, loading, fetchProduct } = useContext(ProductsContext);

  const [quantity, setQuantity] = useState(1);
  const [currentProduct, setCurrentProduct] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadProduct = async () => {
      try {
        const existingProduct = products.find(p => p._id === id);
        if (existingProduct) {
          setCurrentProduct(existingProduct);
          setQuantity(1);
        } else {
          const product = await fetchProduct(id);
          if (!product) throw new Error('Product not found');
          setCurrentProduct(product);
          setQuantity(1);
        }
      } catch (err) {
        console.error('Error loading product:', err);
        setError('Product not found or failed to load.');
      }
    };
    if (id) loadProduct();
  }, [id, products, fetchProduct]);

  const handleAddToCart = () => {
    if (currentProduct.countInStock > 0) {
      addToCart(currentProduct, quantity);
    } else {
      alert('Sorry, this product is currently out of stock.');
    }
  };

  const incrementQuantity = () => {
    if (quantity < currentProduct.countInStock) setQuantity(quantity + 1);
  };

  const decrementQuantity = () => {
    if (quantity > 1) setQuantity(quantity - 1);
  };

  if (loading || !currentProduct) {
    return <div className="loading">Loading product details...</div>;
  }

  if (error) {
    return (
      <div className="error-message">
        <p>{error}</p>
        <button onClick={() => navigate('/products')}>Back to Products</button>
      </div>
    );
  }

  return (
    <motion.div
      className="product-detail-page"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
    >
      <div className="container">
        <button className="back-btn" onClick={() => navigate('/products')}>
          <FaArrowLeft /> Back to Products
        </button>

        <div className="product-detail-container">
          {/* Image */}
          <motion.div
            className="product-image"
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
          >
            <img
              src={currentProduct.image}
              alt={currentProduct.name}
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = '/images/placeholder.svg';
              }}
            />
          </motion.div>

          {/* Info */}
          <motion.div
            className="product-info"
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
          >
            <h1>{currentProduct.name}</h1>
            <p className="product-category">{currentProduct.category}</p>
            <p className="product-price">₹{currentProduct.price.toFixed(2)}</p>

            <div className="product-description">
              <h3>Description</h3>
              <p>{currentProduct.description}</p>
            </div>

            <div className="product-actions">
              <div className="quantity-selector">
                <button onClick={decrementQuantity} disabled={quantity === 1}>
                  <FaMinus />
                </button>
                <span>{quantity}</span>
                <button
                  onClick={incrementQuantity}
                  disabled={quantity === currentProduct.countInStock}
                >
                  <FaPlus />
                </button>
              </div>

              <motion.button
                className="add-to-cart-btn"
                onClick={handleAddToCart}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <FaShoppingCart /> Add to Cart
              </motion.button>
            </div>

            <p className="stock-status">
              Status:{' '}
              <span
                className={
                  currentProduct.countInStock > 0 ? 'in-stock' : 'out-of-stock'
                }
              >
                {currentProduct.countInStock > 0
                  ? 'In Stock'
                  : 'Out of Stock'}
              </span>
            </p>
          </motion.div>
        </div>
      </div>
    </motion.div>
  );
};

export default ProductDetailPage;
