import React, { useState, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { motion } from 'framer-motion';
import { FaShoppingCart, FaMinus, FaPlus } from 'react-icons/fa';
import { CartContext } from '../context/CartContext';
import './ProductDetailPage.css';

const ProductDetailPage = () => {
  const { id } = useParams();
  const { addToCart } = useContext(CartContext);
  const [quantity, setQuantity] = useState(1);

  // Mock product data (would come from API in real app)
  const product = {
    _id: id,
    name: 'Handcrafted Pottery',
    description: 'Beautiful handmade pottery from rural artisans. Each piece is unique and crafted with traditional techniques passed down through generations. The natural clay is sourced locally and fired in traditional kilns.',
    price: 1299,
    image: 'https://images.unsplash.com/photo-1565193298357-c5b64a816c38?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=2670&q=80',
    category: 'Home Decor',
    countInStock: 15
  };

  const handleAddToCart = () => {
    addToCart(product, quantity);
  };

  const incrementQuantity = () => {
    if (quantity < product.countInStock) {
      setQuantity(quantity + 1);
    }
  };

  const decrementQuantity = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  return (
    <motion.div 
      className="product-detail-page"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
    >
      <div className="container">
        <div className="product-detail-container">
          <motion.div 
            className="product-image"
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
          >
            <img src={product.image} alt={product.name} />
          </motion.div>

          <motion.div 
            className="product-info"
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
          >
            <h1>{product.name}</h1>
            <p className="product-category">{product.category}</p>
            <p className="product-price">₹{product.price.toFixed(2)}</p>
            <div className="product-description">
              <h3>Description</h3>
              <p>{product.description}</p>
            </div>

            <div className="product-actions">
              <div className="quantity-selector">
                <button 
                  onClick={decrementQuantity}
                  disabled={quantity === 1}
                >
                  <FaMinus />
                </button>
                <span>{quantity}</span>
                <button 
                  onClick={incrementQuantity}
                  disabled={quantity === product.countInStock}
                >
                  <FaPlus />
                </button>
              </div>

              <motion.button 
                className="add-to-cart-btn"
                onClick={handleAddToCart}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <FaShoppingCart /> Add to Cart
              </motion.button>
            </div>

            <p className="stock-status">
              Status: <span className={product.countInStock > 0 ? 'in-stock' : 'out-of-stock'}>
                {product.countInStock > 0 ? 'In Stock' : 'Out of Stock'}
              </span>
            </p>
          </motion.div>
        </div>
      </div>
    </motion.div>
  );
};

export default ProductDetailPage;
